class CreateAccount extends Component {
    constructor(props) {
        super(props)

        this.state = {
            userid: '',
            pw: '',
        }
    }

    handleSubmit(event) {
        event.preventDefault();
    }

    handleChangeInputUserID = async event => {
        const userid = event.target.value
        this.setState({ userid })
    }

    handleChangeInputPassword = async event => {
        const pw = event.target.value
        this.setState({ pw })
    }

    handleIncludeUser = async () => {
        const {userid, pw} = this.state
        const payload = {userid, pw}

        await api.insertUser(payload).then(res => {
            window.alert(`User inserted successfully`)
            this.setState({
                userid: '',
                pw: '',
            })
        })
    }

    render() {
        const { userid, pw } = this.state
        return (
            <div>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group size="lg" controlId="email">
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                            autoFocus
                            type="text"
                            value={userid}
                            onChange={this.handleChangeInputUserID}
                        />
                    </Form.Group>
                    <Form.Group size="lg" controlId="password">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            type="password"
                            value={pw}
                            onChange={this.handleChangeInputPassword}
                        />
                    </Form.Group>
                    <Button onClick={this.handleIncludeUser} block size="lg" type="submit" disabled={!validateForm()}>
                        Create Account
                    </Button>
                </Form>
            </div>
        )
    }
}

export default CreateAccount
